package com.github.maxicorrea.applicationdesktopbackend.adapters.output.persistense.inmemory;

import java.util.ArrayList;
import java.util.List;

import com.github.maxicorrea.applicationdesktopbackend.adapters.output.persistense.ApplicationDesktopRepository;
import com.github.maxicorrea.applicationdesktopbackend.domain.App;
import com.github.maxicorrea.applicationdesktopbackend.domain.Feedback;
import com.github.maxicorrea.applicationdesktopbackend.domain.Menu;

public class ApplicationDesktopInMemoryRepository implements ApplicationDesktopRepository {

	private static final String IMAGE_BASE64 = "data:image/png;base64,";

	private List<App> apps;

	public ApplicationDesktopInMemoryRepository() {
		apps = new ArrayList<>();
		apps.add(createApp(1, "Java Tips", IMAGE_BASE64, "", ""));
		apps.add(createApp(2, "Java Tips", IMAGE_BASE64, "", ""));
		apps.add(createApp(3, "Java Tips", IMAGE_BASE64, "", ""));
		apps.add(createApp(4, "Java Tips", IMAGE_BASE64, "", ""));
		apps.add(createApp(5, "Java Tips", IMAGE_BASE64, "", ""));
		apps.add(createApp(6, "Java Tips", IMAGE_BASE64, "", ""));
		apps.add(createApp(7, "Java Tips", IMAGE_BASE64, "", ""));
		apps.add(createApp(8, "Java Tips", IMAGE_BASE64, "", ""));
		apps.add(createApp(9, "Java Tips", IMAGE_BASE64, "", ""));
		apps.add(createApp(10, "Java Tips", IMAGE_BASE64, "", ""));
		apps.add(createApp(11, "Java Tips", IMAGE_BASE64, "", ""));
	}

	private App createApp(
			final Integer id, 
			final String name, 
			final String base64, 
			final String description,
			final String url) {
		App app = new App();
		app.setId(id);
		app.setName(name);
		app.setBase64(base64);
		app.setDescription(description);
		app.setUrl(url);
		return app;
	}

	@Override
	public void save(Feedback feedback) {

	}

	@Override
	public Menu load() {
		Menu menu = new Menu();
		menu.setApps(apps);
		return menu;
	}

}
